var scrollTo = function (a, b) { $("html, body").animate({ scrollTop: $(a).offset().top - b }, 650) };
$("#header__more").click(function () { scrollTo("#about", 0) }),
$("#footer__top").click(function () { scrollTo("body", 0) }),
$("#about__scroll").click(function () { scrollTo("#about", 0) }),
$("#faq__scroll").click(function () {scrollTo("#faq", 0)}),
$("#portfolio__scroll").click(function () { scrollTo("#portfolio", 0) });
